(L=5;while true;do readarray -t F< <(find . -type f -print|shuf -n 1);for i in "${!F[@]}";do M=${F[i]};if ffprobe -i "$M" -show_entries format=duration -v quiet -of csv="p=0">/dev/null;then N=$(ffprobe -i "$M" -show_entries format=duration -v quiet -of csv="p=0");D=${N%.*};P=$((D/100*25));R=$((1+($RANDOM%D)-P*2));S=$((P+RANDOM%R));W=$((R/4));LEN=$((1+($RANDOM%L)));mpv "$M" --start=$S --length=$LEN --fs >/dev/null 2>&1 &W=$(echo "$LEN-0.5"|bc);sleep $W;unset 'F[i]';fi;done;done)
